mapper.select:
SELECT u.id, u.username, u.account, u.phone, u.status, u.password, r.stores_id, r.stores_name, r.stores_city, rel.relid, rel.relation_name as relationName FROM ( select * from user
limit ${pageNum}, ${pageSize}
) u LEFT JOIN user_role_relation urr ON urr.user_id = u.id LEFT JOIN stores r ON r.stores_id = urr.stores_id LEFT JOIN relation rel ON rel.relid = urr.relation_id
and concat(u.username, u.account, u.phone) like '%${keyword}%'
and u.`status` = 1

SELECT r.stores_id, r.stores_name, rel.relid, rel.relation_name as relationName FROM ( select * from stores
limit ${pageNum}, ${pageSize}
) r LEFT JOIN user_role_relation urr ON urr.stores_id = r.stores_id LEFT JOIN relation rel ON rel.relid = urr.relation_id LEFT JOIN `user` u ON u.id = urr.user_id WHERE u.id = #{user_id}

mapper.delete:
		delete from user_role_relation
		where user_id = #{user_id}
        and stores_id not in( ${stores_id} )

		delete from user_role_relation
		where user_id = #{user_id}
        and stores_id in ( ${stores_id} )

mapper.select:
  		select relation_id as relationId, stores_id as roleId from user_role_relation
		where user_id = #{user_id}
		and stores_id = #{stores_id}

mapper.update:
update user set
status = 0
where id = #{id}

update admin
set phone = #{phone}
where id = 1

update admin set
password = #{password}
where id = 1

