mapper.select:
SELECT DISTINCT ( select count(1) from machine where m.stores_id = s.stores_id ) as frequency FROM `user` u LEFT JOIN user_role_relation urr ON urr.user_id = u.id LEFT JOIN stores s ON s.stores_id = urr.stores_id LEFT JOIN machine m ON m.stores_id = urr.stores_id where u.`status` = 1
and m.`status` = #{status}
GROUP BY u.account,s.stores_name HAVING 1=1 and frequency != '0'

select SUM(frequency) from ( SELECT u.id as uid, u.username, u.account, u.phone, s.stores_name, s.stores_id, GROUP_CONCAT( DISTINCT m.machine_name ) as machine_name, GROUP_CONCAT( DISTINCT m.machine_id) as machine_id, count(DISTINCT machine_id) as frequency FROM ( select * from `user`
limit ${pageNum}, ${pageSize}
) u LEFT JOIN user_role_relation urr ON urr.user_id = u.id LEFT JOIN stores s ON s.stores_id = urr.stores_id LEFT JOIN machine m ON m.stores_id = urr.stores_id where u.`status` = 1 and s.stores_name is NOT NULL and machine_name is NOT NULL
and CONCAT(u.username, u.account, u.phone, s.stores_name) LIKE '%${keyword}%'
and m.`status` = #{status}
GROUP BY u.account,s.stores_name ) as count GROUP BY uid

SELECT u.id, u.username, u.account, u.phone, s.stores_name, s.stores_id, GROUP_CONCAT( DISTINCT m.machine_name ) as machine_name, GROUP_CONCAT( DISTINCT m.machine_id) as machine_id, count(DISTINCT machine_id) as frequency, ( select GROUP_CONCAT(m.`status`) from machine m where m.stores_id = s.stores_id
and m.`status` = #{status}
GROUP BY m.stores_id ) as `status` FROM ( select * from `user`
limit ${pageNum}, ${pageSize}
) u LEFT JOIN user_role_relation urr ON urr.user_id = u.id LEFT JOIN stores s ON s.stores_id = urr.stores_id LEFT JOIN machine m ON m.stores_id = urr.stores_id where u.`status` = 1 and s.stores_name is NOT NULL and s.stores_name != '' and machine_name is NOT NULL and machine_name != ''
and m.`status` = #{status}
and CONCAT(u.username, u.account, u.phone, s.stores_name) LIKE '%${keyword}%'
GROUP BY u.account,s.stores_name having 1=1

select count(1) from ( select count(1) from ( SELECT m.id, m.stores_id, s.stores_city as stores_city, s.stores_name, GROUP_CONCAT(m.machine_id) as machine_id, GROUP_CONCAT(m.machine_name) as machine_name, count(DISTINCT machine_id) as frequency, ( select GROUP_CONCAT(m.`status`) from machine m where m.stores_id = s.stores_id
and `status` = #{status}
GROUP BY m.stores_id ) as `status` FROM machine m LEFT JOIN stores s ON s.stores_id = m.stores_id where m.stores_id in ( ${content} )
and `status` = #{status}
GROUP BY s.stores_name having 1=1
and CONCAT(s.stores_city, s.stores_name) LIKE '%${keyword}%'
) as count GROUP BY stores_city ) as count

select sum(frequency) from ( SELECT m.id, m.stores_id, s.stores_city, s.stores_name, GROUP_CONCAT(m.machine_id) as machine_id, GROUP_CONCAT(m.machine_name) as machine_name, count(DISTINCT machine_id) as frequency, ( select GROUP_CONCAT(m.`status`) from machine m where m.stores_id = s.stores_id
and `status` = #{status}
GROUP BY m.stores_id ) as `status` FROM machine m LEFT JOIN stores s ON s.stores_id = m.stores_id where m.stores_id in ( ${content} )
and `status` = #{status}
and stores_city in ( ${cityContent} ) GROUP BY s.stores_name having 1=1
and CONCAT(s.stores_city, s.stores_name) LIKE '%${keyword}%'
) as count group by stores_city

SELECT m.id, m.stores_id, s.stores_city as stores_city, s.stores_name, GROUP_CONCAT(m.machine_id) as machine_id, GROUP_CONCAT(m.machine_name) as machine_name, count(DISTINCT machine_id) as frequency, ( select GROUP_CONCAT(m.`status`) from machine m where m.stores_id = s.stores_id
and `status` = #{status}
GROUP BY m.stores_id ) as `status` FROM machine m LEFT JOIN stores s ON s.stores_id = m.stores_id where m.stores_id in ( ${content} )
and `status` = #{status}
and stores_city in ( ${cityContent} ) GROUP BY s.stores_name having 1=1
and CONCAT(s.stores_city, s.stores_name) LIKE '%${keyword}%'
