mapper.select:
select SUM(frequency) from ( SELECT u.account, s.stores_city, s.stores_name, s.stores_id, GROUP_CONCAT( DISTINCT m.machine_name ) as machine_name, GROUP_CONCAT( DISTINCT m.machine_id) as machine_id, count(DISTINCT machine_id) as frequency, ( select GROUP_CONCAT(m.`status`) from machine m where m.stores_id = s.stores_id
and m.`status` = #{status}
GROUP BY m.stores_id ) as `status` FROM ( select * from `user` ) u LEFT JOIN user_role_relation urr ON urr.user_id = u.id LEFT JOIN stores s ON s.stores_id = urr.stores_id LEFT JOIN machine m ON m.stores_id = urr.stores_id where u.`status` = 1 and m.stores_id not in ( ${content} ) and u.account = #{account}
and m.`status` = #{status}
and stores_city in ( ${cityContent} ) GROUP BY u.account, s.stores_name having 1=1
and CONCAT(s.stores_city, s.stores_name) LIKE '%${keyword}%'
) as sum GROUP BY stores_city

SELECT u.account, s.stores_city as stores_city, s.stores_name, s.stores_id, GROUP_CONCAT( DISTINCT m.machine_name ) as machine_name, GROUP_CONCAT( DISTINCT m.machine_id) as machine_id, count(DISTINCT machine_id) as frequency, ( select GROUP_CONCAT(m.`status`) from machine m where m.stores_id = s.stores_id
and m.`status` = #{status}
GROUP BY m.stores_id ) as `status` FROM ( select * from `user` ) u LEFT JOIN user_role_relation urr ON urr.user_id = u.id LEFT JOIN stores s ON s.stores_id = urr.stores_id LEFT JOIN machine m ON m.stores_id = urr.stores_id where u.`status` = 1 and u.account = #{account} and m.stores_id not in ( ${content} )
and m.`status` = #{status}
and stores_city in ( ${cityContent} ) GROUP BY u.account, s.stores_name having 1=1
and CONCAT(s.stores_city, s.stores_name) LIKE '%${keyword}%'

select count(1) from ( select count(1) from ( SELECT u.account, s.stores_name, s.stores_id, s.stores_city as stores_city, GROUP_CONCAT( DISTINCT m.machine_name ) as machine_name, GROUP_CONCAT( DISTINCT m.machine_id) as machine_id, count(DISTINCT machine_id) as frequency, ( select GROUP_CONCAT(m.`status`) from machine m where m.stores_id = s.stores_id
and m.`status` = #{status}
GROUP BY m.stores_id ) as `status` FROM ( select * from `user` ) u LEFT JOIN user_role_relation urr ON urr.user_id = u.id LEFT JOIN stores s ON s.stores_id = urr.stores_id LEFT JOIN machine m ON m.stores_id = urr.stores_id where u.`status` = 1 and u.account = #{account} and m.stores_id not in ( ${content} )
and m.`status` = #{status}
GROUP BY u.account,s.stores_name having 1=1
and CONCAT(s.stores_city, s.stores_name) LIKE '%${keyword}%'
) as count GROUP BY stores_city ) as count

